view_spec.config:
{
    "view": {
        "content": {
            "url": {
                "path": "image",
                "type": "data.input"
            },
            "fullHeight": true,
            "type": "view.image"
        },
        "controls": {
            "items": [
                {
                    "content": "Find the same **shoes** on Marks and Spencer",
                    "type": "view.markdown"
                },
                {
                    "action": {
                        "payload": "https://www.marksandspencer.com",
                        "type": "action.open-link"
                    },
                    "label": "Marks and Spencer",
                    "validation": {
                        "url": "https://www.marksandspencer.com",
                        "type": "condition.link-opened"
                    },
                    "type": "view.action-button"
                },
                {
                    "content": "Shoes must be the same color and the same style.",
                    "type": "view.text"
                },
                {
                    "data": {
                        "path": "found_link",
                        "type": "data.output"
                    },
                    "label": "Paste the link here",
                    "validation": {
                        "conditions": [
                            {
                                "type": "condition.required"
                            },
                            {
                                "data": {
                                    "path": "found_link",
                                    "type": "data.output"
                                },
                                "schema": {
                                    "type": "string",
                                    "pattern": "marksandspencer.com/?"
                                },
                                "hint": "the link must be from the Marks and Spencer website",
                                "type": "condition.schema"
                            }
                        ],
                        "type": "condition.all"
                    },
                    "type": "field.text"
                }
            ],
            "type": "view.list"
        },
        "controlsWidth": 320.0,
        "type": "layout.sidebar"
    },
    "plugins": [
        {
            "layout": {
                "kind": "scroll",
                "taskWidth": 800.0
            },
            "type": "plugin.toloka"
        }
    ]
}
