view_spec.config:
{
    "view": {
        "items": [
            {
                "url": {
                    "path": "image",
                    "type": "data.input"
                },
                "fullHeight": true,
                "type": "view.image"
            },
            {
                "items": [
                    {
                        "items": [
                            {
                                "content": {
                                    "path": "left_link",
                                    "type": "data.input"
                                },
                                "type": "view.text"
                            },
                            {
                                "action": {
                                    "payload": {
                                        "path": "left_link",
                                        "type": "data.input"
                                    },
                                    "type": "action.open-link"
                                },
                                "label": "Go to site",
                                "type": "view.action-button"
                            },
                            {
                                "url": {
                                    "path": "left_link",
                                    "type": "data.input"
                                },
                                "fullHeight": true,
                                "type": "view.iframe"
                            }
                        ],
                        "type": "view.list"
                    },
                    {
                        "items": [
                            {
                                "content": {
                                    "path": "right_link",
                                    "type": "data.input"
                                },
                                "type": "view.text"
                            },
                            {
                                "action": {
                                    "payload": {
                                        "path": "right_link",
                                        "type": "data.input"
                                    },
                                    "type": "action.open-link"
                                },
                                "label": "Go to site",
                                "type": "view.action-button"
                            },
                            {
                                "url": {
                                    "path": "right_link",
                                    "type": "data.input"
                                },
                                "fullHeight": true,
                                "type": "view.iframe"
                            }
                        ],
                        "type": "view.list"
                    }
                ],
                "type": "layout.side-by-side"
            },
            {
                "content": "Which photo is the most similar to the original one?",
                "type": "view.text"
            },
            {
                "data": {
                    "path": "result",
                    "type": "data.output"
                },
                "options": [
                    {
                        "value": {
                            "path": "left_link",
                            "type": "data.input"
                        },
                        "label": "The left one is better"
                    },
                    {
                        "value": {
                            "path": "right_link",
                            "type": "data.input"
                        },
                        "label": "The right one is better"
                    }
                ],
                "validation": {
                    "type": "condition.required"
                },
                "type": "field.radio-group"
            }
        ],
        "type": "view.list"
    },
    "plugins": [
        {
            "1": {
                "data": {
                    "path": "result",
                    "type": "data.output"
                },
                "payload": {
                    "path": "left_link",
                    "type": "data.input"
                },
                "type": "action.set"
            },
            "2": {
                "data": {
                    "path": "result",
                    "type": "data.output"
                },
                "payload": {
                    "path": "right_link",
                    "type": "data.input"
                },
                "type": "action.set"
            },
            "type": "plugin.hotkeys"
        }
    ]
}
